document.addEventListener("DOMContentLoaded",function(){const slides=document.querySelectorAll(".hero-slide");const dots=document.querySelectorAll(".hero-dot");const prevButton=document.querySelector(".hero-arrow-left");const nextButton=document.querySelector(".hero-arrow-right");let currentSlide=0;let slideInterval;const slideDelay=5e3;function showSlide(index,direction="next"){const oldSlide=currentSlide;slides[oldSlide].classList.remove("active");dots[oldSlide].classList.remove("active");if(direction==="next"){slides[oldSlide].classList.add("slide-out-left")}else{slides[oldSlide].classList.add("slide-out-right")}currentSlide=index;if(direction==="next"){slides[currentSlide].classList.add("slide-in-right")}else{slides[currentSlide].classList.add("slide-in-left")}slides[currentSlide].offsetHeight;slides[currentSlide].classList.remove("slide-in-right","slide-in-left");slides[currentSlide].classList.add("active");dots[currentSlide].classList.add("active");setTimeout(()=>{slides[oldSlide].classList.remove("slide-out-left","slide-out-right")},600)}function nextSlide(){const next=(currentSlide+1)%slides.length;showSlide(next,"next")}function prevSlide(){const prev=(currentSlide-1+slides.length)%slides.length;showSlide(prev,"prev")}function startSlideshow(){slideInterval=setInterval(nextSlide,slideDelay)}function stopSlideshow(){clearInterval(slideInterval)}if(nextButton){nextButton.addEventListener("click",()=>{stopSlideshow();nextSlide();startSlideshow()})}if(prevButton){prevButton.addEventListener("click",()=>{stopSlideshow();prevSlide();startSlideshow()})}dots.forEach((dot,index)=>{dot.addEventListener("click",()=>{stopSlideshow();const direction=index>currentSlide?"next":"prev";showSlide(index,direction);startSlideshow()})});if(slides.length>0){startSlideshow()}window.addEventListener("scroll",function(){const header=document.querySelector(".header");if(window.scrollY>100){header.style.background="rgba(255, 255, 255, 0.95)";header.style.backdropFilter="blur(10px)"}else{header.style.background="#fff";header.style.backdropFilter="none"}});const observerOptions={threshold:.1,rootMargin:"0px 0px -50px 0px"};const observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity="1";entry.target.style.transform="translateY(0)"}})},observerOptions);const serviceCards=document.querySelectorAll(".service-card");serviceCards.forEach((card,index)=>{card.style.opacity="0";card.style.transform="translateY(30px)";card.style.transition=`opacity 0.6s ease ${index*.2}s, transform 0.6s ease ${index*.2}s`;observer.observe(card)});const aboutFeatures=document.querySelectorAll(".feature");aboutFeatures.forEach((feature,index)=>{feature.style.opacity="0";feature.style.transform="translateY(30px)";feature.style.transition=`opacity 0.6s ease ${index*.2}s, transform 0.6s ease ${index*.2}s`;observer.observe(feature)});const projectInfo=document.querySelector(".project-info");const projectImage=document.querySelector(".project-image");if(projectInfo){projectInfo.style.opacity="0";projectInfo.style.transform="translateX(30px)";projectInfo.style.transition="opacity 0.6s ease, transform 0.6s ease";observer.observe(projectInfo)}if(projectImage){projectImage.style.opacity="0";projectImage.style.transform="translateX(-30px)";projectImage.style.transition="opacity 0.6s ease, transform 0.6s ease";observer.observe(projectImage)}});